CREATE TABLE IF NOT EXISTS case_submissions (
    id SERIAL PRIMARY KEY,
    application_scenarios TEXT,
    technical_requirements TEXT,
    technology_stack TEXT,
    city_size VARCHAR(100),
    budget_range VARCHAR(100),
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);

CREATE TABLE IF NOT EXISTS blockchain_cases (
    id SERIAL PRIMARY KEY,
    case_name TEXT NOT NULL,
    application_scenarios TEXT NOT NULL,
    technical_requirements JSON NOT NULL,
    technology_stack JSON NOT NULL,
    city_size TEXT CHECK(city_size IN ('small', 'medium', 'large')),
    budget_range JSON NOT NULL   -- [min,max]
);



